function sumbitForm(){$("#AddAnnounsmentForm").ajaxSubmit({success:()=>{toastr.success("Запись успешно сохранена","Успех"),modalHide()},error:()=>{toastr.error("Внутреняя ошибка","Ошибка"),modalHide()}})}function modalHide(){var n=$("#modal-placeholder");n.find(".modal").modal("hide")}$(document).ready(function(){document.getElementById("AddAnnounsmentForm").addEventListener("submit",function(n){n.preventDefault();sumbitForm()})});