function sumbitForm(){$("#UpdateAnnounsment").ajaxSubmit({success:()=>{toastr.success("Запись успешно сохранена","Успех"),modalHide()},error:()=>{toastr.error("Внутреняя ошибка","Ошибка"),modalHide()}})}function modalHide(){var n=$("#modal-placeholder");n.find(".modal").modal("hide")}function removeAnnounsment(n){confirm(" Удалить записи  ")&&($.ajax({url:"/Announsments/Delete",type:"POST",data:{ids:n},success:()=>{toastr.success("Запись успешно удалена","Успех")},error:()=>{toastr.error("Внутреняя ошибка","Ошибка")}}),$("#table").bootstrapTable("remove",{field:"id",values:n}),$("#table").bootstrapTable("refresh",{url:"/Announsments/GetAnnounsmentsJson"}))}$(document).ready(function(){document.getElementById("UpdateAnnounsment").addEventListener("submit",function(n){n.preventDefault();sumbitForm()})});